      <button className="form-field" type="submit" onClick={(e) => handleLoading(e)}>
        Pull
      </button>
      <button className="form-field" type="submit">
        Update
      </button>     
      </div>      
    </form>
    <div className='ausgabe'>
      {person ? person.map((p) =>  <ListItem key={p.id} props={p} />) : <></>}
    </div>
  </div>
);
}

export default App;
